sw $t3, ($t1)

addi $t1,$t1,-4 
sw $t4, ($t1)

addi $t1,$t1,-4 
sw $t4, ($t1)

addi $t1,$t1,-4 
sw $t3, ($t1)

addi $t1,$t1,-124 
sw $t4, ($t1)

addi $t1,$t1,4 
sw $t4, ($t1)

jr $ra

keyboardChanges:
li $t9, 0xffff0000 
lw $t8, 0($t9) 
beq $t8, 1, keypress_happened 
jr $ra
keypress_happened:
lw $t3, 4($t9) # this assumes $t9 is set to 0xfff0000
beq $t3, 0x61, res_a # ASCII code of 'a' is 0x61 or 97 in decimal
beq $t3, 0x77, res_w
beq $t3, 0x73, res_s
beq $t3, 0x64 ,res_d
jr $ra

res_a:
lw $s3, leftDis
lw $s4, rightDis
bne $s3,4,DrawA
jr $ra

res_w:
lw $s3, upDis
lw $s4, downDis
bne $s3,12,DrawW
jr $ra

res_s:
lw $s3, upDis
lw $s4, downDis
bne $s4,12,DrawS
jr $ra

res_d:
lw $s3, leftDis
lw $s4, rightDis
bne $s4,4,DrawD

jr $ra

DrawS:
la $a0, defaultCharX #get address
addi $a1, $s0, 256
sw $a1 0($a0) #save new value
addi $a1,$zero,0
addi $a0, $zero, 0

la $a0, downDis #get address
addi $a1, $s4, 1
sw $a1 0($a0) #save new value

addi $a1,$zero,0
addi $a0, $zero, 0

la $a0, upDis #get address
addi $a1, $s3, -1
sw $a1 0($a0) #save new value
  
jr $ra 


DrawW:
la $a0, defaultCharX #get address
addi $a1, $s0, -256
sw $a1 0($a0) #save new value
addi $a1,$zero,0
addi $a0, $zero, 0
la $a0, downDis #get address
addi $a1, $s4, -1
sw $a1 0($a0) #save new value

addi $a1,$zero,0
addi $a0, $zero, 0

la $a0, upDis #get address
addi $a1, $s3, 1
sw $a1 0($a0) #save new value
jr $ra

DrawD:
la $a0, defaultCharX #get address
addi $a1, $s0, 32
sw $a1 0($a0) #save new value
addi $a1,$zero,0
addi $a0, $zero, 0
la $a0, rightDis #get address
addi $a1, $s4, 1
sw $a1 0($a0) #save new value

addi $a1,$zero,0
addi $a0, $zero, 0

la $a0, leftDis #get address
addi $a1, $s3, -1
sw $a1 0($a0) #save new value
jr $ra

DrawA:
la $a0, defaultCharX #get address
addi $a1, $s0, -32
sw $a1 0($a0) #save new value
addi $a1,$zero,0
addi $a0, $zero, 0
la $a0, rightDis #get address
addi $a1, $s4, -1
sw $a1 0($a0) #save new value

addi $a1,$zero,0
addi $a0, $zero, 0

la $a0, leftDis #get address
addi $a1, $s3, 1
sw $a1 0($a0) #save new value

jr $ra
